import { CART_ACTION_TYPE } from "./cart.types"

export const cartItemsReducer = (state, action) => {
   const {type, payload} = action

   switch (type) {
      case(CART_ACTION_TYPE.SET_CART_ITEM) :
         return {...state , payload}
      default:
         state
   }
}
