import { USER_ACTION_TYPE } from "./user.types"
import { createAction } from "../../utils/reducer/reducer.utils"

export const setCurrentUser = (user) =>
   createAction(USER_ACTION_TYPE.SET_CURRENT_USER , user)


/*
export const USER_ACTION_TYPE = {
   SET_CURRENT_USER : 'SET_CURRENT_USER',
   CHECK_USER_SESSION: 'CHECK_USER_SESSION',
   SIGN_IN_EMAIL_START: 'SIGN_IN_EMAIL_START',
   SIGN_IN_GOOGLE_START: 'SIGN_IN_EMAIL_START',
   SIGN_IN_SUCCESS: 'SIGN_IN_EMAIL_START',
}
*/

export const checkUserSession = () => createAction(USER_ACTION_TYPE.CHECK_USER_SESSION);

export const signInGoogleStart = () => createAction(USER_ACTION_TYPE.SIGN_IN_GOOGLE_START);

export const signInEmailStart = (email, password) => createAction(USER_ACTION_TYPE.SIGN_IN_EMAIL_START, {email, password});

export const signInSuccess = (user) => createAction(USER_ACTION_TYPE.SIGN_IN_SUCCESS, user);

export const signInFaild = (error) => createAction(USER_ACTION_TYPE.SIGN_IN_FAILED, error)
