import styled from 'styled-components'

export const Div_ProductContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;

  img {
    width: 100%;
    height: 95%;
    object-fit: cover;
    margin-bottom: 5px;
  }



  &:hover {
    img {
      opacity: 0.8;
    }


`

export const Div_Footer = styled.div`
width: 100%;
height: 5%;
display: flex;
justify-content: space-between;
font-size: 18px;
`

export const Span_Name = styled.span`
  width: 90%;
  margin-bottom: 15px;
`
export const Span_Price = styled.span`
  width: 10%;
`
