import { useContext } from 'react'
import { CartContext } from "../../context/cart.context";
import './checkout-item.scss'

const CheckOutItem = ({ product }) => {
  const { cartItems, goToCart, decreaseCartItem } = useContext(CartContext)
  const { name, price, quantity, imageUrl } = product

  return (
    <div className='checkout-item-container'>
      <div className='image-container'>
        <img src={imageUrl} alt={`${name}`} />
      </div>
      <span className='name'>{name}</span>
      <span className='quantity'> {quantity} </span>
      <span className='' onClick={() => goToCart(product)}>increase</span>
      <span className='' onClick={() => decreaseCartItem(product)}>decrease</span>
      <span className='price'>{price}</span>
      <div className='remove-buttin'>&#10005;</div>
    </div>
  )
}

export default CheckOutItem;
