import { useContext } from 'react'
import { CartContext } from "../../context/cart.context";


const CheckOut = () => {
  const { cartItems, goToCart } = useContext(CartContext)

  return (
    <div>
      {cartItems.map((each) => {
        const {id, name, quantity, price } = each
        return (
          <div key={id}>
            <h2>{name}</h2>
            <span> {quantity} </span>
            <br />
            <span onClick={() => goToCart(each)}>increase</span>
            <br />
            <span>decrease</span>
            <br />
            <span>{price}</span>
          </div>
        )
      }
      )}
    </div>
  )
}

export default CheckOut;
