    }


    return (
        <div>
            <h1>회원가입 : 아래 양식을 입력해주세요.</h1>
            <form onSubmit={(form) => {submitHandler}}>
                <label>아이디</label>
                <input 
                    onChange={changeHandler} 
                    required 
                    type='text'
                    name='displayName'
                ></input>

                <label>이메일</label>
                <input 
                    onChange={changeHandler} 
                    required 
                    type='email'
                    name='email'
                ></input>

                <label>비밀번호</label>
                <input
                    onChange={changeHandler} 
                    required 
                    type='password'
                    name='password'
                ></input>

                <label>비밀번호 확인</label>
                <input 
                    onChange={changeHandler} 
                    required 
                    type='password'
                    name='confirmPassword'
                ></input>
                <button type='submit'>가입신청</button>
            </form>
        </div>
    )
}

export default SignUpForm;
