import { useContext } from 'react'
import { CartContext } from "../../context/cart.context";

import './checkout.scss';


const CheckOut = () => {
  const { cartItems, goToCart, decreaseCartItem } = useContext(CartContext)

  return (
    <div className='checkout-container'>
      <div className='checkout-header'>
        <div className='header-block'>
          <span>Product</span>
        </div>
        <div className='header-block'>
          <span>Description</span>
        </div>
        <div className='header-block'>
          <span>Quantity</span>
        </div>
        <div className='header-block'>
          <span>Price</span>
        </div>
        <div className='header-block'>
          <span>Remove</span>
        </div>
      </div>
      {cartItems.map((each) => {
        const { id, name, quantity, price } = each
        return (
          <div key={id}>
            <h2>{name}</h2>
            <span> {quantity} </span>
            <br />
            <span onClick={() => goToCart(each)}>increase</span>
            <br />
            <span onClick={() => decreaseCartItem(each)}>decrease</span>
            <br />
            <span>{price}</span>
          </div>
        )
      }
      )}
      <span className='Total'>Total: 0</span>
    </div>
  )
}

export default CheckOut;
