import { useEffect } from 'react';
import { getRedirectResult } from "firebase/auth";
import SignUpForm from '../../component/sign-up-form/sign-up-form';
import SignInForm from '../../component/sign-in-form/sign-in-form';
import Button from '../../component/button/button';

import {
    signIn_GooglePopup,
    setUserDbFromAuth,
    auth
} from "../../utils/firebase/firebase";


const SignIn = () => {

    const logUser_Google = async () => {
        const { user } = await signIn_GooglePopup();
        const userDocRef = await setUserDbFromAuth(user);
    }


    return (
        <div>
            <div>
                <h2>이미 계정이 있으시군요</h2>
                <span>이메일로 로그인하기</span>
                <SignInForm logGoogle={logUser_Google} />
            </div>
            <div>
                <SignUpForm />
            </div>
        </div>
    )
}

export default SignIn;
