import { takeLatest, put, all, call } from "redux-saga/effects";
import  {getCurrentUser, setUserDbFromAuth} from '../../utils/firebase/firebase'
import { signInFaild, signInSuccess } from "./user.action";
import { USER_ACTION_TYPE } from "./user.types";




export function* getSnapshotFromUser (userAuth, additionalDetails) {
   try {
      const userSnapshot = yield call(setUserDbFromAuth, userAuth, additionalDetails)

      console.log(userSnapshot)
      console.log(userSnapshot.data())
      // if (userSnapshot) {
      //    yield put(signInSuccess(userSnapshot({userSnapshot.id: userAuth.id})))
      // }
   } catch(error) {
      yield put(signInFaild(error));
   }
}

export function* isUserAuthentication () {
   try {
      const userAuth = yield call(getCurrentUser, userAuth);
      if(!userAuth) return;
      yield call(getSnapshotFromUser)
   } catch (error) {
      yield put(signInFaild(error))
   }

}

export function* onCheckUserSession () {
   yield takeLatest(USER_ACTION_TYPE.SIGN_IN_SUCCESS, call(isUserAuthentication))
}

export function* userSaga () {
   yield all([call(onCheckUserSession)]);
}
