import { takeLatest, put, all, call } from "redux-saga/effects";
import  {
   getCurrentUser, 
   setUserDbFromAuth,
   signIn_GooglePopup
} from '../../utils/firebase/firebase'
import { signInFaild, signInSuccess } from "./user.action";
import { USER_ACTION_TYPE } from "./user.types";




export function* getSnapshotFromUser (userAuth, additionalDetails) {
   try {
      const userSnapshot = yield call(
         setUserDbFromAuth, 
         userAuth, 
         additionalDetails
      ) 
      console.log('userSnapshot :', userSnapshot)
      yield put(signInSuccess({id: userSnapshot.id, ...userSnapshot.data()}))
   } catch(error) {
      yield put(signInFaild(error));
   }
}

export function* signInWithGoogle () {
   try {
      const userAuth = yield call(signIn_GooglePopup);
      console.log('google userAuth :', userAuth);
      const user = userAuth.user;

      yield call(getSnapshotFromUser, user);

   } catch (error) {
      yield put(signInFaild(error));
   }
}

export function* signInWithEmail ({payload: {email, password}}) {
   try {
      const userAuth = yield call(signInUserAuthWithEmailAndPassword, email, password)
      yield call(getSnapshotFromUser, userAuth)
   }  catch (error) {
      yield put(signInFaild(error))
   }
}

export function* isUserAuthenticated () {
   try {
      const {user} = yield call(getCurrentUser)
      if(!userAuth) return
      yield call(getSnapshotFromUser, user)
   } catch (error) {
      yield put(signInFaild(error))
   }
}

export function* onGoogleSignInStart() {
   yield takeLatest(USER_ACTION_TYPE.SIGN_IN_GOOGLE_START, signInWithGoogle)
}


export function* onEmailSignInStart () {
   yield takeLatest(USER_ACTION_TYPE.SIGN_IN_EMAIL_START, signInWithEmail)
}

export function* onCheckUserSession () {
   yield takeLatest(USER_ACTION_TYPE.CHECK_USER_SESSION, isUserAuthenticated)
}

export function* userSaga () {
   yield all([
      call(onCheckUserSession),
      call(onGoogleSignInStart),
   ]);
}
