import { useContext } from "react"
import { CartContext } from "../../context/cart.context"

const CheckOutItem = ({cartItem}) => {
  const { name, quantity, price, id } = cartItem

  const {checkoutDelete} = useContext(CartContext)

  const deleteItem = (idToDel) => {
    checkoutDelete(idToDel)
  }

  return (
    <div>
      <h2>{name}</h2>
      <div>
        <span> {quantity} </span>
      </div>
      <div>
        <span>{price}</span>
      </div>
      <span onClick={deleteItem(id)}> x </span>
    </div>
  )



}

export default CheckOutItem;
