import {useState} from 'react';
import {signInUserAuthWithEmailAndPassword} from '../../utils/firebase/firebase';
import InputForm from '../input-form/input-form';
import Button from '../button/button';
import './sign-in-form.scss'



const defaultFormFields = { 
  email : '',
  password : '',
}

const SignInForm = ({logGoogle}) => {
  const [getStateUser, setStateUser] = useState(defaultFormFields);
  const {email, password} = getStateUser;

  const changeHandler = (event) => {
    const name = event.target.name
    const value = event.target.value
    setStateUser({...getStateUser, [name]:value})
  }

  const submitHandler = async () => {
    const response = signInUserAuthWithEmailAndPassword(email, password);
    console.log(response);
  }  


  return (
    <div className='sign-in-container'>
      <form onSubmit={submitHandler}>
        <InputForm
          label='이메일'
          onChange={changeHandler}
          type='email'
          name='email'
          value={email}
        ></InputForm>

        <InputForm
          label='비밀번호'
          onChange={changeHandler}
          type='password'
          name='password'
          value={password}
        ></InputForm>
      </form>

      <div className='buttons-container'>
        <Button type='submit'>로그인</Button>
        <Button
          type='submit'
          onClick={logGoogle}  
          buttonType='google'
        > 
          구글로 로그인하기
        </Button>
      </div>
    </div>
  )
}

export default SignInForm;
