import { useState } from 'react'
import { createUserAuthWithEmailAndPassword } from '../../utils/firebase/firebase';

const defaultFormFields = { 
        displayName : '',
        email : '',
        password : '',
        confirmPassword : '',
    }

const SignUpForm = () => {
    const [getUserState, setUserState] = useState(defaultFormFields);
    const {displayName, email, password, confirmPassword} = getUserState;
    // console.log('state :', getUserState);

    const changeHandler = (event) => {
        const name = event.target.name
        const value = event.target.value

        setUserState({...getUserState, [name]:value});
    }

    const submitHandler = async (event) => {
        event.preventDefault();

        if (password !== confirmPassword) {
            alert('비밀번호와 비밀번호 확인은 같아야 합니다.');
            return;
        }

        try {
            const response = await createUserAuthWithEmailAndPassword(email, password);
            console.log(response);
        } catch (err) {
            console.log(err)
        }
    }


    return (
        <div>
            <h1>회원가입 : 아래 양식을 입력해주세요.</h1>
            <form onSubmit={(form) => {submitHandler(form)}}>
                <label>아이디</label>
                <input 
                    onChange={changeHandler} 
                    required 
                    type='text'
                    name='displayName'
                ></input>

                <label>이메일</label>
                <input 
                    onChange={changeHandler} 
                    required 
                    type='email'
                    name='email'
                ></input>

                <label>비밀번호</label>
                <input
                    onChange={changeHandler} 
                    required 
                    type='password'
                    name='password'
                ></input>

                <label>비밀번호 확인</label>
                <input 
                    onChange={changeHandler} 
                    required 
                    type='password'
                    name='confirmPassword'
                ></input>
                <button type='submit'>가입신청</button>
            </form>
        </div>
    )
}

export default SignUpForm;
